#include <limits.h>
#include "leetcode75.h"

struct TreeNode
{
	int val;
	struct TreeNode *left;
	struct TreeNode *right;
};

/**
 * 437. Path Sum III
 *
 * Constraints:
 * 	The number of nodes in the tree is in the range [0, 1000].
 * 	-10^9 <= Node.val <= 10^9
 * 	-1000 <= targetSum <= 1000
 */
int pathSum(struct TreeNode *root, int targetSum);

#define MAX_STACK_SIZE 10000

typedef struct TreeNode TreeNode;

enum
{
	null = INT_MIN
};

struct s_input
{
	int *arr;
	int arrSize;
	int targetSum;
	int expected;
};

typedef struct s_stack_el
{
	TreeNode *node;
	int idx;
} stack_el;

void free_tree(TreeNode *root)
{
	TreeNode *node;
	TreeNode *stack[MAX_STACK_SIZE];
	int stackSize = 0;

	if (root == NULL) return;

	stack[stackSize++] = root;

	while (stackSize != 0)
	{
		node = stack[--stackSize];
		if (node->right != NULL)
			stack[stackSize++] = node->right;
		if (node->left != NULL)
			stack[stackSize++] = node->left;
		free(node);
	}
}

TreeNode *new_node(int data)
{
	TreeNode *node = (TreeNode *) malloc(sizeof(TreeNode));
	node->val = data;
	node->left = NULL;
	node->right = NULL;
	return (node);
}

TreeNode *getTree(const int *arr, int size)
{
	TreeNode *root;
	TreeNode *node;
	int left_index;
	int right_index;

	int sp = 0;
	stack_el el;
	stack_el stack[MAX_STACK_SIZE];

	/* Edge case for an empty tree */
	if (size == 0 || arr[0] == null)
		return NULL;

	root = new_node(arr[0]);

	stack[sp++] = (stack_el) {root, 0};
	while (sp != 0)
	{
		el = stack[--sp];  // Pop the stack

		node = el.node;
		left_index = 2 * el.idx + 1;
		right_index = 2 * el.idx + 2;

		if (left_index < size && arr[left_index] != null)
		{
			node->left = new_node(arr[left_index]);
			stack[sp++] = (stack_el) {node->left, left_index};
		}

		if (right_index < size && arr[right_index] != null)
		{
			node->right = new_node(arr[right_index]);
			stack[sp++] = (stack_el) {node->right, right_index};
		}
	}
	return (root);
}

static int ft_do_test(struct s_input *input)
{
	TreeNode *root;
	int result;
	int check_val;

	ft_print_int_tab_null(input->arr, input->arrSize, null, NULL);

	root = getTree(input->arr, input->arrSize);

	result = pathSum(root, input->targetSum);
	check_val = (result == input->expected);
	if (!check_val)
	{
		printf("got \"%d\" whilst \"%d\" was to be expected\n",
			   result, input->expected);
		check_val = false;
	}
	check(check_val);
	free_tree(root);
	return (0);
}

